CREATE TABLE TB_APARTAMENTO (ID INTEGER AUTO_INCREMENT NOT NULL, ID_CLIENTE INTEGER, ID_CONDOMINIO INTEGER, NUMERO INTEGER NOT NULL, SERIAL_HIDROMETRO VARCHAR(255), NOME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TB_CLIENTE (ID INTEGER AUTO_INCREMENT NOT NULL, ATIVO TINYINT(1) default 0, EMAIL VARCHAR(255), ID_APARTAMENTO INTEGER, ID_ENDERECO INTEGER, NOME VARCHAR(150) NOT NULL, NOME_REFERENCIA VARCHAR(50) NOT NULL, OBSERVACAO VARCHAR(255), TELEFONE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TB_CONDOMINIO (ID INTEGER AUTO_INCREMENT NOT NULL, ATIVO TINYINT(1) default 0, CNPJ VARCHAR(14), FIMCONTRATO DATE, ID_ENDERECO INTEGER NOT NULL, INICIOCONTRATO DATE, INSCRICAO_ESTADUAL VARCHAR(20), INSCRICAO_MUNICIPAL VARCHAR(20), NOME VARCHAR(150) NOT NULL, POCO TINYINT(1) default 0 NOT NULL, TAXA_LEITURA DECIMAL(10,2) NOT NULL, TELEFONE1 VARCHAR(11) NOT NULL, TELEFONE2 VARCHAR(11), PRIMARY KEY (ID))
CREATE TABLE TB_CONSUMO_AGUA (ID INTEGER AUTO_INCREMENT NOT NULL, DT_HR_UPDATE DATETIME NOT NULL, ID_APARTAMENTO INTEGER NOT NULL, ID_CLIENTE INTEGER, LEITURA INTEGER NOT NULL, MES DATE NOT NULL, PRIMARY KEY (ID))
CREATE TABLE TB_EMPRESA (ID INTEGER AUTO_INCREMENT NOT NULL, CNPJ VARCHAR(255), EMAIL1 VARCHAR(255), EMAIL2 VARCHAR(255), ID_ENDERECO INTEGER, INSCRICAO_ESTADUAL VARCHAR(255), INSCRICAO_MUNICIPAL VARCHAR(255), NOME VARCHAR(150) NOT NULL, NOME_REFERENCIA VARCHAR(50) NOT NULL, TELEFONE1 VARCHAR(255), TELEFONE2 VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TB_ENDERECO (ID INTEGER AUTO_INCREMENT NOT NULL, BAIRRO VARCHAR(255), CEP VARCHAR(255), CIDADE VARCHAR(255), ESTADO VARCHAR(255), RUA_NUMERO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TB_FUNCIONARIO (ID INTEGER AUTO_INCREMENT NOT NULL, EMAIL VARCHAR(100) NOT NULL, NOME VARCHAR(150) NOT NULL, NOME_REFERENCIA VARCHAR(50) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE LOG (ID INTEGER NOT NULL, DATA DATETIME NOT NULL, MENSAGEM VARCHAR(255), TIPO VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE TB_SINDICO (ID INTEGER AUTO_INCREMENT NOT NULL, EMAIL1 VARCHAR(255), EMAIL2 VARCHAR(255), DT_SAIDA DATE, DT_ENTRADA DATE, NOME VARCHAR(150) NOT NULL, NOME_REFERENCIA VARCHAR(50) NOT NULL, TELEFONE1 VARCHAR(255), TELEFONE2 VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TB_TORRE (ID INTEGER AUTO_INCREMENT NOT NULL, ID_CONDOMINIO INTEGER NOT NULL, NOME VARCHAR(20) NOT NULL, NUMERO INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
